using Microsoft.EntityFrameworkCore;using Mvc.BookManagement.Models.Entities;namespace Mvc.BookManagement.Data{    public class ApplicationDbContext : DbContext    {        public ApplicationDbContext(DbContextOptions<ApplicationDbContext> options)            : base(options)        {        }        public DbSet<Book> Books { get; set; }        protected override void OnModelCreating(ModelBuilder modelBuilder)        {            base.OnModelCreating(modelBuilder);                    modelBuilder.Entity<Book>(entity =>            {                entity.ToTable("Books");                entity.HasKey(e => e.Id);                entity.Property(e => e.Title).IsRequired().HasMaxLength(100);                entity.Property(e => e.Author).IsRequired().HasMaxLength(50);                entity.Property(e => e.Genre).IsRequired().HasMaxLength(30);                entity.Property(e => e.PageCount).IsRequired();            });        }    }}