// Helpers/CacheHelper.csusing Microsoft.Extensions.Caching.Memory;using System;namespace Mvc.BookManagement.Helpers{    public static class CacheHelper    {        public static MemoryCacheEntryOptions GetDefaultCacheOptions()        {            return new MemoryCacheEntryOptions()                .SetSlidingExpiration(TimeSpan.FromMinutes(5))                .SetAbsoluteExpiration(DateTimeOffset.Now.AddMinutes(30));        }    }}