// Helpers/ResultHandlerFactory.csusing Microsoft.AspNetCore.Mvc;using Microsoft.Extensions.Localization;using Mvc.BookManagement.Interfaces;using Mvc.BookManagement.Resources.SharedResources;namespace Mvc.BookManagement.Helpers{    public class ResultHandlerFactory : IResultHandlerFactory    {        private readonly IStringLocalizer<SharedResource> _localizer;        public ResultHandlerFactory(IStringLocalizer<SharedResource> localizer)        {            _localizer = localizer;        }        public IResultHandler Create(Controller controller)        {            return new ResultHandler(controller, _localizer);        }    }}