// Helpers/ValidationHelper.csusing Ardalis.Result;using FluentValidation;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace Mvc.BookManagement.Helpers{    public static class ValidationHelper    {        public static async Task<Result> ValidateAsync<T>(IValidator<T> validator, T model)        {            var validationResult = await validator.ValidateAsync(model);                        if (!validationResult.IsValid)            {                List<ValidationError> errors = validationResult.Errors                    .Select(e => new ValidationError                     {                         Identifier = e.PropertyName,                         ErrorMessage = e.ErrorMessage                     }).ToList();                                return Result.Invalid(errors);            }                        return Result.Success();        }    }}