using Ardalis.Result;using Mvc.BookManagement.Models.DTOs;namespace Mvc.BookManagement.Interfaces;public interface IBookService{    Task<Result<IEnumerable<BookDTO>>> GetAllBooksAsync(CancellationToken cancellationToken = default);    Task<Result<BookDTO>> GetBookByIdAsync(int id, CancellationToken cancellationToken = default);    Task<Result<BookDTO>> CreateBookAsync(BookInputDTO bookInput, CancellationToken cancellationToken = default);    Task<Result<BookDTO>> UpdateBookAsync(int id, BookInputDTO bookInput, CancellationToken cancellationToken = default);    Task<Result> DeleteBookAsync(int id, CancellationToken cancellationToken = default);}