using AutoMapper;using Mvc.BookManagement.Models.DTOs;using Mvc.BookManagement.Models.Entities;using Mvc.BookManagement.Models.ViewModels;namespace Mvc.BookManagement.Mappings{    public class MappingProfile : Profile    {        public MappingProfile()        {            CreateMap<Book, BookDTO>();            CreateMap<BookInputDTO, Book>();                        CreateMap<BookDTO, BookViewModel>();                        CreateMap<BookCreateViewModel, BookInputDTO>();            CreateMap<BookUpdateViewModel, BookInputDTO>();                        CreateMap<BookDTO, BookUpdateViewModel>();        }    }}