using AutoMapper;using Mvc.BookManagement.Models.DTOs;using Mvc.BookManagement.Models.Entities;namespace Mvc.BookManagement.Mappings;public class MappingProfile : Profile{    public MappingProfile()    {        // Map from entity to DTO        CreateMap<Book, BookDTO>();                    // Map from input DTO to entity        CreateMap<BookInputDTO, Book>();    }}