using AutoMapper;using Mvc.BookManagement.Models.DTOs;using Mvc.BookManagement.Models.Entities;using Mvc.BookManagement.Models.ViewModels;namespace Mvc.BookManagement.Mappings{    public class MappingProfile : Profile    {        public MappingProfile()        {            // Entity <-> DTO mappings            CreateMap<Book, BookDTO>();            CreateMap<BookInputDTO, Book>();                        // DTO <-> ViewModel mappings            CreateMap<BookDTO, BookViewModel>();                        // Form ViewModel -> DTO mappings            CreateMap<BookCreateViewModel, BookInputDTO>();            CreateMap<BookUpdateViewModel, BookInputDTO>();                        // DTO -> Edit ViewModel mapping            CreateMap<BookDTO, BookUpdateViewModel>();        }    }}