using System.ComponentModel.DataAnnotations;namespace Mvc.BookManagement.Models.Entities;public class Book{    [Key]    public int Id { get; set; }    [StringLength(100)]    public string Title { get; set; } = "Empty Book Title";    [StringLength(50)]    public string Author{ get; set; } = "Empty Book Author";    [StringLength(30)]    public string Genre { get; set; } = "Empty Book Genre";    [MaxLength(1000)]    [Range(10, 30000)]    public int PageCount { get; set; }}